<template>
    <li style="position: relative;">
      <router-link v-if="node.hash" :to="node.hash">
        {{node.text}}
        <div  v-if="level === 3" class="lplay3 horizontal-line"></div>
        <div  v-if="level === 2" class="lplay32 horizontal-line" :style="transformObject"></div>
      </router-link>
      <a href="javascript:void 0" v-if="!node.hash" @click="foldMenu">
        {{node.text}}
        <img v-if="node.icon" :src="icon[node.icon]" class="licon"></img>
        <div v-if="level === 1" class="lplay triangle-left" :style="transformObject"></div>
        <div v-if="level === 2" class="lplay2 triangle-Hollow-left" :style="transformObject"></div>
      </a>
      <ul v-if="isFolder" v-show="open">
         <item v-for="(n, i) of node.nodes" :node="n" :level="level + 1" :key="i"></item>
      </ul>
    </li>
</template>
/**
* @author ： 秦超 on 2018年5月25日
*
* 入参：
* node : 菜单
*
**/
<script>

export default {
  name: 'item',
  props: {
    node: {
      type: Object,
      default: null,
    },
    level: {
      type: Number,
      default: 1,
    },
  },
  data() {
    return {
      open: false,
      transformObject: {
        transition: 'transform 0.2s linear',
        transform: 'rotate(0deg)',
      },
      icon: {
        qiye: 'data:image/png;base64,',
        qrcode: 'data:image/png;base64,',
        renzhengguan: 'data:image/png;base64,',
        shenbao: 'data:image/png;base64,',
        notice: 'data:image/png;base64,',
        setup: 'data:image/png;base64,',
        wodebutie: 'data:image/png;base64,',
      },
    };
  },
  computed: {
    isFolder() {
      return this.node.nodes && this.node.nodes.length;
    },
  },
  methods: {
    foldMenu() {
      if (!this.open) {
        this.transformObject.transform = 'rotate(90deg)';
      } else {
        this.transformObject.transform = 'rotate(0deg)';
      }
      this.open = !this.open;
    },
  },
};
</script>

